import React from "react";
import { Link } from "react-router-dom";


const Card = ({ odontologo }) => {


  const addFav = () => {

    const listaDeCards = [];

    const data = JSON.parse(window.localStorage.getItem("card"));
    if (data) {
      listaDeCards = [...data]
    }
    console.log(listaDeCards);
    listaDeCards.push(odontologo)
    localStorage.setItem("card", JSON.stringify(listaDeCards))

    console.log(odontologo);
    alert(`${odontologo.name} ha sido guardado como favorito`)
  }

  return (

    <div className="card">
      <img className="imagen-odontologo" src="./images/doctor.jpg" alt='Logo Doctor' />
      <p>{odontologo.name}</p>
      <p>{odontologo.id}</p>
      <p>{odontologo.username}</p>
      <Link to={`/dentist/${odontologo.id}`}>Detalle</Link>
      <button onClick={addFav} className="favButton">Add fav</button>
    </div>
  );
};

export default Card;
