  const [cardFav, SetCardFav] = useState([]);

  const getLocalStorage = () => {
    return JSON.parse(localStorage.getItem("card"))
    
  }

  useEffect(() => {
    const datosStorage = getLocalStorage();
    if (datosStorage) {
      console.log(datosStorage)
      
      SetCardFav(datosStorage);

    }
  }, [])

