import React from "react";
import { Link } from "react-router-dom";


const Card = ({ odontologo }) => {

  const validateLocalStorage = () => {

    const listaDeCards = [];
    const data = JSON.parse(localStorage.getItem("card"));

    if (data != null) {

      data.map((element) => {
        listaDeCards.push(element)
      }
      )

    }
    return listaDeCards;
  }

  const saveFavorite = (clave, valor) => {

    localStorage.setItem("card", JSON.stringify(listaDeCards))

    console.log(odontologo);
    alert(`${odontologo.name} ha sido guardado como favorito`)
    return "Guardado o no guardado"
  }
 
const addFav = () => {

  const listaDeCards = validateLocalStorage();
  listaDeCards.push(odontologo);

  listaDeCards = saveFavorite("card", listaDeCards);

}
  return (

    <div className="card">
      <img className="imagen-odontologo" src="./images/doctor.jpg" alt='Logo Doctor' />
      <p>{odontologo.name}</p>
      <p>{odontologo.id}</p>
      <p>{odontologo.username}</p>
      <Link to={`/dentist/${odontologo.id}`}>Detalle</Link>
      <button onClick={addFav} className="favButton">Add fav</button>
    </div>
  );
};

export default Card;
